import React, { useEffect, useState } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

const App = () => {
    const [cursos, setCursos] = useState([]);
    const [curso, setCurso] = useState({ titulo: "", creditos: "", descripcion: "" });

    useEffect(() => {
        axios.get("http://127.0.0.1:8000/api/cursos/")
            .then(response => setCursos(response.data))
            .catch(error => console.error("Error:", error));
    }, []);

    const handleChange = (e) => {
        setCurso({ ...curso, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.post("http://127.0.0.1:8000/api/cursos/", curso)
            .then(response => {
                setCursos([...cursos, response.data]);
                setCurso({ titulo: "", creditos: "", descripcion: "" });
            })
            .catch(error => console.error("Error:", error));
    };

    return (
        <div className="container mt-4">
            <h1 className="text-center text-primary">Gestión de Cursos</h1>

            {/* Formulario */}
            <form className="mb-4" onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label className="form-label">Título</label>
                    <input type="text" className="form-control" name="titulo" value={curso.titulo} onChange={handleChange} required />
                </div>
                <div className="mb-3">
                    <label className="form-label">Créditos</label>
                    <input type="number" className="form-control" name="creditos" value={curso.creditos} onChange={handleChange} required />
                </div>
                <div className="mb-3">
                    <label className="form-label">Descripción</label>
                    <textarea className="form-control" name="descripcion" value={curso.descripcion} onChange={handleChange} required></textarea>
                </div>
                <button type="submit" className="btn btn-success">Agregar Curso</button>
            </form>

            {/* Lista de Cursos */}
            <table className="table table-striped">
                <thead className="table-dark">
                    <tr>
                        <th>ID</th>
                        <th>Título</th>
                        <th>Créditos</th>
                        <th>Descripción</th>
                    </tr>
                </thead>
                <tbody>
                    {cursos.length > 0 ? (
                        cursos.map((c) => (
                            <tr key={c.id}>
                                <td>{c.id}</td>
                                <td>{c.titulo}</td>
                                <td>{c.creditos}</td>
                                <td>{c.descripcion}</td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="4" className="text-center">No hay cursos</td>
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
    );
};

export default App;
